import React, {Component}  from 'react';
import axios from 'axios';

const emailRegx = RegExp(/^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/);
const phoneRegx = /^\d{10}$/;
 const formValid = validate =>{

 let valid = true;

 Object.values(validate).forEach(val =>{

  val.length > 0 && (valid = false);   

 });
 return valid;
 };


class Register extends Component {
    constructor(props) {
        super(props)
        this.state = { 

           register:{
            firstname:'',
            lastname:'',
            email:'',
            password:'',
            gender:'',
            role:''
           },
           validate:{
            firstname:'',
            lastname:'',
            email:'',
            password:'',
            gender:'',
            uid:'',
            role:'',
           }

         }
    }

onsubmit=(event)=>{

    event.preventDefault();

    if(formValid){


    const students = {
        firstname : this.state.insertedData.firstname,
        lastname : this.state.insertedData.lastname,
        email:this.state.insertedData.email,
        password:this.state.insertedData.password,
        post : 'yes'

       }; 

    axios({
        method: 'POST',
        url: `http://localhost/react-app/api/getdata.php`,
        headers: { 'content-type': 'application/json' },
        data: students

        // then(result => {
        // // this.setState( { 
        // // mailSent: result.data.sent
        // // })
        // console.log('hliiii');
        // })
        // .catch(error => this.setState( { error: error.message } ));
      });


    }


}

onchange=(event)=>{

    event.preventDefault();

    const {name,value} = event.target;
      let validate = this.state.validate;
      let register = this.state.register;

      switch(name){

          case 'firstname':
          validate.firstname = 
          value.length < 3
          ? "minimum 3 Characters requires":'';
          break;

          case 'lastname':
          validate.lastname = 
          value.length < 3 
          ? "minimum 3 Characters requires":'';
          break;

          case 'email':
          validate.email = 
          emailRegx.test(value)
          ? "":'invalid email address';
          break;

          case 'password':
          validate.password = 
          value.length < 6  
          ? "minimum 6 Characters requires":'';
          break;

          case 'gender':
          validate.gender = 
          value.length < 3
          ? "please select a gender":'';
          break;

          case 'phone':
          validate.gender = 
          phoneRegx.test(value)?"":"enter a valid number";
          break;

          case 'uid':
          validate.uid = 
          value.length < 3
          ? "enter a valid uid":'';
          break;

          case 'role':
          validate.role = 
          value.length < 3
          ? "please select a role":'';
          break;

          default:
          break;


      }

      switch(name){

        case 'firstname':
        validate.firstname = 
        value;
        break;

        case 'lastname':
        validate.lastname = 
        value;
        break;

        case 'email':
        validate.email =value;
        break;

        case 'password':
        validate.password = 
        value;
        break;

        case 'gender':
        validate.gender = 
        value;
        break;

        case 'phone':
        validate.gender = value;
        break;

        case 'uid':
        validate.uid = 
        value;
        break;

        case 'role':
        validate.role = 
        value;
        break;

        default:
        break;


    }


   this.setState({ validate, [name]:value}, ()=> console.log(this.state));


};




    render() { 
        return (           
            <div className="page-wrapper bg-gra-02 p-t-130 p-b-100 font-poppins">
        <div className="wrapper wrapper--w680">
            <div className="card card-4">
                <div className="card-body">
                    <h2 className="title">Registration Form</h2>
                    <form>
                        <div className="row row-space">
                            <div className="col-6">
                                <div className="input-group">
                                    <label className="label">first name</label>
                                    <input className="input--style-4" onChange={this.onchange} type="text" name="firstname"/>
                                </div>
                            </div>
                            <div className="col-6">
                                <div className="input-group">
                                    <label className="label">last name</label>
                                    <input className="input--style-4" type="text" onChange={this.onchange} name="lastname"/>
                                </div>
                            </div>
                        </div>
                        <div className="row row-space">
                            <div className="col-6">
                                <div className="input-group">
                                    <label className="label">Email</label>
                                    <input className="input--style-4" type="email" onChange={this.onchange} name="email"/>
                                </div>
                            </div>
                            <div className="col-6">
                                <div className="input-group">
                                    <label className="label">Password</label>
                                    <input className="input--style-4" type="text" onChange={this.onchange} name="password"/>
                                </div>
                            </div>

                        </div>
                        <div className="row row-space">
                        <div className="col-6">
                                <div className="input-group">
                                    <label className="label">Phone Number</label>
                                    <input className="input--style-4" type="text" onChange={this.onchange} name="phone"/>
                                </div>
                            </div>
                            <div className="col-6">
                              <label className="label">Gender</label>
                                <div className="input-group">
                                    <div className="p-t-10">
                                        <label className="radio-container m-r-45">Male
                                            <input type="radio" onChange={this.onchange}  name="gender" value="male"/>
                                            <span className="checkmark"></span>
                                        </label>
                                        <label className="radio-container">Female
                                            <input type="radio" onChange={this.onchange} name="gender" value="female"/>
                                            <span className="checkmark"></span>
                                        </label>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="col-12">  
                           <div className="col-6">
                                <div className="input-group">
                                    <label className="label">UID</label>
                                    <input className="input--style-4" type="text" onChange={this.onchange} name="uid"/>
                                </div>
                            </div>
                            <div className="col-6">
                                <label className="label">Role</label>
                                <div className="input-group">                   
                                    <div className="rs-select2 js-select-simple select--no-search">
                                        <select name="role" onChange={this.onchange} >
                                            <option disabled="disabled" >Choose option</option>
                                            <option value="Student">Student</option>
                                            <option value="Teacher">Teacher</option>
                                            <option value="Librarian">Librarian</option>
                                        </select>
                                        <div className="select-dropdown"></div>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="p-t-15">
                            <button className="btn btn--radius-2 btn--blue" type="submit" onClick={this.onsubmit} >Submit</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>

        );
    }
}

export default Register;
